import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SalesSection.css';

const Sale = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [carData, setCarData] = useState({
    manufacturer: '',
    model: '',
    year: '',
    price: '',
    driveType: '',
    fuel: '',
    transmission: '',
    seats: '',
    kilometers: '',
    vehicleType: '',
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  const togglePopup = () => {
    setIsPopupOpen(!isPopupOpen);
  };

  const handleInputChange = (e) => {
    if (!isLoggedIn) {
      togglePopup();
      return;
    }
    setCarData({ ...carData, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    if (!isLoggedIn) {
      togglePopup();
      return;
    }
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSuccessMessage('');
    setErrorMessage('');

    if (!isLoggedIn) {
      togglePopup();
      return;
    }

    const formData = new FormData();
    Object.keys(carData).forEach((key) => formData.append(key, carData[key]));
    if (image) {
      formData.append('image', image);
    }

    try {
      const response = await axios.post('http://localhost:5000/add-car', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (response.status === 201) {
        setSuccessMessage(response.data.message);
        setCarData({
          manufacturer: '',
          model: '',
          year: '',
          price: '',
          driveType: '',
          fuel: '',
          transmission: '',
          seats: '',
          kilometers: '',
          vehicleType: '',
        });
        setImage(null);
        setPreview('');
      } else {
        setErrorMessage(response.data.message || 'Failed to upload car');
      }
    } catch (error) {
      console.error('Error uploading car:', error);
      setErrorMessage('An error occurred while uploading the car');
    }
  };

  return (
    <div className="sale-page">
      <h2>Sell Your Car</h2>
      {!isLoggedIn && <p className="warning-message">⚠️ You must log in to list a car!</p>}

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <h3>Car Details</h3>
        <input type="text" name="manufacturer" placeholder="Manufacturer" value={carData.manufacturer} onChange={handleInputChange} disabled={!isLoggedIn} />
        <input type="text" name="model" placeholder="Model" value={carData.model} onChange={handleInputChange} disabled={!isLoggedIn} />
        <input type="number" name="year" placeholder="Year" value={carData.year} onChange={handleInputChange} disabled={!isLoggedIn} />
        <input type="number" name="price" placeholder="Price" value={carData.price} onChange={handleInputChange} disabled={!isLoggedIn} />
        <input type="number" name="kilometers" placeholder="Kilometers Driven" value={carData.kilometers} onChange={handleInputChange} disabled={!isLoggedIn} />

        <select name="driveType" value={carData.driveType} onChange={handleInputChange} disabled={!isLoggedIn}>
          <option value="">Drive Type</option>
          <option value="FWD">Front-Wheel Drive</option>
          <option value="RWD">Rear-Wheel Drive</option>
          <option value="AWD">All-Wheel Drive</option>
          <option value="4WD">4-Wheel Drive</option>
        </select>

        <select name="fuel" value={carData.fuel} onChange={handleInputChange} disabled={!isLoggedIn}>
          <option value="">Fuel Type</option>
          <option value="Gasoline">Gasoline</option>
          <option value="Diesel">Diesel</option>
          <option value="Electric">Electric</option>
          <option value="Hybrid">Hybrid</option>
        </select>

        <select name="transmission" value={carData.transmission} onChange={handleInputChange} disabled={!isLoggedIn}>
          <option value="">Transmission</option>
          <option value="Automatic">Automatic</option>
          <option value="Manual">Manual</option>
          <option value="CVT">CVT</option>
        </select>

        <select name="vehicleType" value={carData.vehicleType} onChange={handleInputChange} disabled={!isLoggedIn}>
          <option value="">Vehicle Type</option>
          <option value="Sedan">Sedan</option>
          <option value="SUV">SUV</option>
          <option value="Truck">Truck</option>
          <option value="Van">Van</option>
          <option value="Coupe">Coupe</option>
        </select>

        <input type="number" name="seats" placeholder="Number of Seats" value={carData.seats} onChange={handleInputChange} disabled={!isLoggedIn} />

        <h3>Upload Car Image</h3>
        <input type="file" accept="image/*" onChange={handleImageChange} disabled={!isLoggedIn} />

        {preview && <img src={preview} alt="Car Preview" className="image-preview" />}

        <button type="submit" disabled={!isLoggedIn}>List Car for Sale</button>
      </form>

      {successMessage && <p className="success-message">{successMessage}</p>}
      {errorMessage && <p className="error-message">{errorMessage}</p>}

      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-content">
            <p>You need to log in or register to list a car.</p>
            <button onClick={() => window.location.href = "/login"}>Go to Login/Register</button>
            <button onClick={togglePopup}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sale;
