import React, { useState } from 'react';
import './Buy.css';

const Buy = () => {
  const [showMoreFilters, setShowMoreFilters] = useState(false);
  const [manufacturer, setManufacturer] = useState('');
  const [model, setModel] = useState('');
  const [fuel, setFuel] = useState('');
  const [priceFrom, setPriceFrom] = useState('');
  const [priceTo, setPriceTo] = useState('');
  const [color, setColor] = useState('');
  const [interiorColor, setInteriorColor] = useState('');
  const [interiorMaterial, setInteriorMaterial] = useState('');
  const [transmission, setTransmission] = useState('');
  const [driveType, setDriveType] = useState('');
  const [doors, setDoors] = useState('');
  const [features, setFeatures] = useState({
    airbags: false,
    seatWarmers: false,
    sunroof: false,
    parkingSensors: false,
    backupCamera: false,
    navigation: false
  });

  // Dummy car listings data
  const carListings = [
    {
      id: 1,
      manufacturer: 'Volkswagen',
      model: 'Golf',
      year: 2020,
      kilometers: 10000,
      price: 20000,
      fuel: 'Petrol',
      driveType: 'FWD',
      transmission: 'Automatic',
      horsepower: 150,
      imgSrc: 'https://via.placeholder.com/300',
    },
    {
      id: 2,
      manufacturer: 'Toyota',
      model: 'Corolla',
      year: 2019,
      kilometers: 15000,
      price: 18000,
      fuel: 'Hybrid',
      driveType: 'RWD',
      transmission: 'Manual',
      horsepower: 130,
      imgSrc: 'https://via.placeholder.com/300',
    }
  ];

  // Toggle to show/hide more filters
  const toggleMoreFilters = () => {
    setShowMoreFilters(!showMoreFilters);
  };

  const handleFeatureChange = (feature) => {
    setFeatures((prevFeatures) => ({
      ...prevFeatures,
      [feature]: !prevFeatures[feature],
    }));
  };

  const filteredCars = carListings.filter(car => {
    return (
      (!manufacturer || car.manufacturer === manufacturer) &&
      (!model || car.model === model) &&
      (!fuel || car.fuel === fuel) &&
      (!color || car.color === color) &&
      (!transmission || car.transmission === transmission) &&
      (!driveType || car.driveType === driveType) &&
      (!priceFrom || car.price >= priceFrom) &&
      (!priceTo || car.price <= priceTo)
    );
  });

  return (
    <div className="buy-page">
      {/* Filters Section on Top */}
      <div className="filters-section-top">
        <div className="filters">
          {/* Common filters */}
          <div className="filter">
            <label>Manufacturer</label>
            <select value={manufacturer} onChange={e => setManufacturer(e.target.value)}>
              <option value="">Any</option>
              <option value="Volkswagen">Volkswagen</option>
              <option value="Toyota">Toyota</option>
              <option value="BMW">BMW</option>
            </select>
          </div>

          <div className="filter">
            <label>Model</label>
            <select value={model} onChange={e => setModel(e.target.value)}>
              <option value="">Any</option>
              <option value="Golf">Golf</option>
              <option value="Corolla">Corolla</option>
              <option value="X5">X5</option>
            </select>
          </div>

          <div className="filter">
            <label>Fuel Type</label>
            <select value={fuel} onChange={e => setFuel(e.target.value)}>
              <option value="">Any</option>
              <option value="Petrol">Petrol</option>
              <option value="Diesel">Diesel</option>
              <option value="Hybrid">Hybrid</option>
              <option value="Electric">Electric</option>
            </select>
          </div>

          <div className="filter">
            <label>Price (From)</label>
            <input
              type="number"
              value={priceFrom}
              placeholder="Min Price"
              onChange={e => setPriceFrom(e.target.value)}
            />
          </div>

          <div className="filter">
            <label>Price (To)</label>
            <input
              type="number"
              value={priceTo}
              placeholder="Max Price"
              onChange={e => setPriceTo(e.target.value)}
            />
          </div>

          {/* Button to show/hide more filters */}
          <button className="toggle-more-filters" onClick={toggleMoreFilters}>
            {showMoreFilters ? 'Hide Filters' : 'More Filters'}
          </button>
        </div>

        {/* More Filters Section */}
        {showMoreFilters && (
          <div className="more-filters-section">
            {/* Exterior Color */}
            <div className="filter">
              <label>Exterior Color</label>
              <select value={color} onChange={e => setColor(e.target.value)}>
                <option value="">Any</option>
                <option value="Black">Black</option>
                <option value="White">White</option>
                <option value="Red">Red</option>
                <option value="Blue">Blue</option>
              </select>
            </div>

            {/* Transmission */}
            <div className="filter">
              <label>Transmission</label>
              <select value={transmission} onChange={e => setTransmission(e.target.value)}>
                <option value="">Any</option>
                <option value="Automatic">Automatic</option>
                <option value="Manual">Manual</option>
              </select>
            </div>

            {/* Drive Type */}
            <div className="filter">
              <label>Drive Type</label>
              <select value={driveType} onChange={e => setDriveType(e.target.value)}>
                <option value="">Any</option>
                <option value="FWD">Front-Wheel Drive (FWD)</option>
                <option value="RWD">Rear-Wheel Drive (RWD)</option>
                <option value="AWD">All-Wheel Drive (AWD)</option>
              </select>
            </div>
          </div>
        )}
      </div>

      {/* Car Listings Section */}
      <div className="car-listings-section">
        <h2>Car Listings</h2>
        <div className="car-listings">
          {filteredCars.length > 0 ? (
            filteredCars.map(car => (
              <div key={car.id} className="car-listing">
                <img src={car.imgSrc} alt={car.model} />
                <div className="car-details">
                  <h3>{car.manufacturer} {car.model}</h3>
                  <p><strong>Year:</strong> {car.year}</p>
                  <p><strong>Kilometers:</strong> {car.kilometers} km</p>
                  <p><strong>Fuel Type:</strong> {car.fuel}</p>
                  <p><strong>Drive Type:</strong> {car.driveType}</p>
                  <p><strong>Transmission:</strong> {car.transmission}</p>
                  <p><strong>Horsepower:</strong> {car.horsepower} HP</p>
                  <button>Contact Seller</button>
                </div>
              </div>
            ))
          ) : (
            <p>No cars found matching your filters.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Buy;
